import 'dart:io';

void main\(\) {
  final filepath = r'c:\\Users\\marti\\OneDrive\\Escritorio\\flutter toro-rider\\toro driver flutter\\toro_driver\\lib\\src\\screens\\organizer\\organizer_event_dashboard_screen.dart';
  final file = File\(filepath\);
  final lines = file.readAsLinesSync\(\);

  // Find method boundaries
  final deadMethodNames = {
    '_buildCompactKPIs',
    '_buildMiniKPI',
    '_buildItinerarySection',
    '_showStopDetails', 
    '_buildDetailRow',
    '_buildPassengersRealtime',
    '_buildKPIsGrid',
    '_buildKPICard',
    '_buildMapSection',
    '_buildMapMarkers',
    '_buildCheckInsSection',
    '_buildCheckInRow',
  };

  // Find start of each dead method
  final methodStarts = <int, String>{};
  for \(int i = 0; i < lines.length; i++\) {
    final trimmed = lines[i].trim\(\);
    for \(final name in deadMethodNames\) {
      if \(trimmed.contains\('$name\('\) && 
          \(trimmed.startsWith\('Widget '\) || trimmed.startsWith\('void '\) || 
           trimmed.startsWith\('List<'\) || trimmed.startsWith\('Future<'\)\)\) {
        methodStarts[i] = name;
      }
    }
  }

  // Find end of each method by counting braces
  int findMethodEnd\(int startIdx\) {
    int braceCount = 0;
    bool started = false;
    for \(int i = startIdx; i < lines.length; i++\) {
      for \(final ch in lines[i].split\(''\)\) {
        if \(ch == '{'\) braceCount++;
        if \(ch == '}'\) braceCount--;
      }
      if \(braceCount > 0\) started = true;
      if \(started && braceCount == 0\) return i;
    }
    return startIdx;
  }

  // Build set of lines to remove
  final linesToRemove = <int>{};

  for \(final entry in methodStarts.entries\) {
    final start = entry.key;
    final name = entry.value;
    final end = findMethodEnd\(start\);

    // Include comment lines before the method
    int actualStart = start;
    while \(actualStart > 0 && lines[actualStart - 1].trim\(\).startsWith\('///'\)\) {
      actualStart--;
    }
    // Include blank line before comment
    if \(actualStart > 0 && lines[actualStart - 1].trim\(\).isEmpty\) {
      actualStart--;
    }

    for \(int i = actualStart; i <= end; i++\) {
      linesToRemove.add\(i\);
    }

    print\('DELETE $name: lines ${actualStart + 1}-${end + 1} \(${end - actualStart + 1} lines\)'\);
  }

  // Build new file content
  final newLines = <String>[];
  for \(int i = 0; i < lines.length; i++\) {
    if \(!linesToRemove.contains\(i\)\) {
      newLines.add\(lines[i]\);
    }
  }

  print\('\\nOriginal: ${lines.length} lines'\);
  print\('Removed: ${linesToRemove.length} lines'\);
  print\('New: ${newLines.length} lines'\);

  // Write back
  file.writeAsStringSync\(newLines.join\('\\n'\)\);
  print\('\\nFile updated successfully!'\);
}
DARTEOF)

